import { useEffect, useState } from "react";

export default function Todo({ todos, setTodos }) {
  function handleDelete(id) {
    return function () {
      setTodos(todos.filter((todo) => todo.id !== id));
    };
  }

  function handleComplete(id) {
    return function () {
      let comp = todos.find((todo) => todo.id === id);
      setTodos((comp.complete = true));
    };
  }
  return (
    <div>
      {todos.map((todo) => (
        <div>
          <ul className="list-group w-50 mt-5 mb-5 m-auto" key={todo.id}>
            <li
              onClick={handleComplete(todo.id)}
              className={`list-group-item ${todo.complete && "completed"}`}
            >
              {todo.id} - {todo.title}
            </li>
            <li className="list-group-item">
              <button
                onClick={handleDelete(todo.id)}
                className="btn btn-danger"
                type="button"
              >
                X
              </button>
            </li>
          </ul>
        </div>
      ))}
    </div>
  );
}
