import React, {useState, useEffect} from 'react'
import {Link} from "react-router-dom";
import Buscador from "../components/Buscador"

function Candidatos() {
    const [candidatos, setCandidatos] = useState([]);

    useEffect(
        function () {
          async function fetchApi() {
            let response = await fetch("http://localhost:8080/candidatos", {
                mode: "cors",
              }
            );
            let json = await response.json();
            setCandidatos(json);
          }
    
          fetchApi();
        },
        []
      );

      if (!candidatos) {
        return (
          <div className="text-center">
            <div
              className="spinner-grow"
              style={{ width: "3rem", height: "3rem" }}
              role="status"
            >
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        );
      }
  return (
      <div>
           <h1 className="h1 my-5 text-center animate__animated animate__fadeInUp">
                Candidatos Releevant
              </h1>
        <div className="container">
          <div className="row justify-content-between">
            <div className="col-9">
            <Buscador />
            </div>
            <div className="col">
              <Link to="/anadir" className="btn btn-warning">
                AÃ±adir nuevo candidato
              </Link>
            </div>
          </div>
        </div>
      {candidatos.map((candidato) => {
          return(
            <div>
            <div class="row my-5 m-auto">
       <div class="col-4">
         <div class="list-group" id="list-tab" role="tablist">
           <a class="list-group-item list-group-item-action active" id="list-home-list" data-toggle="list" href="#list-home" role="tab" aria-controls="home">Home</a>
           <a class="list-group-item list-group-item-action" id="list-profile-list" data-toggle="list" href="#list-profile" role="tab" aria-controls="profile">Profile</a>
           <a class="list-group-item list-group-item-action" id="list-messages-list" data-toggle="list" href="#list-messages" role="tab" aria-controls="messages">Messages</a>
           <a class="list-group-item list-group-item-action" id="list-settings-list" data-toggle="list" href="#list-settings" role="tab" aria-controls="settings">Settings</a>
         </div>
       </div>
       <div class="col-8">
         <div class="tab-content" id="nav-tabContent">
           <div class="tab-pane fade show active" id="list-home" role="tabpanel" aria-labelledby="list-home-list">...</div>
           <div class="tab-pane fade" id="list-profile" role="tabpanel" aria-labelledby="list-profile-list">...</div>
           <div class="tab-pane fade" id="list-messages" role="tabpanel" aria-labelledby="list-messages-list">...</div>
           <div class="tab-pane fade" id="list-settings" role="tabpanel" aria-labelledby="list-settings-list">...</div>
         </div>
       </div>
     </div> 
         </div>
          )
      })}

    </div>
  )
}

export default Candidatos
