import React from "react";
import "../App.css";
import { Link, useNavigate } from "react-router-dom";
import { useGlobalContext } from "../contexts/GlobalContext";
import Buscador from "../components/Buscador";
import "animate.css";

function Eventos() {
  const { eventos, setEventos } = useGlobalContext();
  let navigate = useNavigate()

  async function handleDelete(_id) {
    let response = await fetch(`http://localhost:8080/delete/evento/${_id}`, {
      mode: "cors",
      method: "DELETE",
    });
    let json = await response.json();
    setEventos(json);
    navigate("/eventos");
  }

  if (!eventos) {
    return (
      <div className="text-center">
        <div
          className="spinner-grow"
          style={{ width: "3rem", height: "3rem" }}
          role="status"
        >
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }
  return (
    <div className="container">
      <div className="row mb-5">
      <h1 className="h1 my-5 text-center animate__animated animate__fadeInUp">
                Eventos Releevant
              </h1>
        <div className="container">
          <div className="row justify-content-between">
            <div className="col-9">
            <Buscador />
            </div>
            <div className="col">
              <Link to="/anadir" className="btn btn-warning">
                Añadir nuevo evento
              </Link>
            </div>
          </div>
        </div>
        
        {eventos?.map((evento) => {
          return (
            <div class="card mb-3"key={evento._id}>
            <img
                  src={evento.foto}
                  className="card-img-top embed-responsive-item"
                  alt="..."
                  width={"100%"}
                />
            <div class="card-body">
              <h3 class="card-title">{evento.titulo}</h3>
              <h6 class="card-title">{evento.ponentes}</h6>
              <p class="card-text">{evento.descripcion}</p>
              <p class="card-text"><small class="text-muted">{evento.fecha}</small></p>
              <h6>Info interna</h6>
              <p>
                    <b>Asistentes inscritos</b>:{" "}
                    {evento.asistentes.inscrito}
                  </p>
                  <p>
                    <b>Asistentes reales</b>:{" "}
                    {evento.asistentes.reales}
                  </p>
              <button
              onClick={() => handleDelete(evento._id)}
              className="btn btn-warning w-100 m-auto"
            >
              Delete evento
            </button>
            </div>
            
          </div>
          );
        })}
      </div>
    </div>
  );
}

export default Eventos;
