import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Buscador from "../components/Buscador";

function Candidatos() {
  const [candidatos, setCandidatos] = useState([]);
  const [datos, setDatos] = useState("");
  const [infoRel, setInfoRel] = useState("");
  const [infoCon, setInfoCon] = useState("");
  const [coments, setComents] = useState("");
  

  useEffect(function () {
    async function fetchApi() {
      let response = await fetch("http://localhost:8080/candidatos", {
        mode: "cors",
      });
      let json = await response.json();
      setCandidatos(json);
    }

    fetchApi();
  }, []);

  function datosPeronales(_id) {
    var datosPer = candidatos.find((d) => d._id === _id);
    setDatos(datosPer._id);
   console.log(datosPer)
  }

 /*  function infoReleevant(_id) {
    var infoRelee = candidatos.find((d) => d._id === _id);
    setInfoRel(infoRelee._id);
  }

  function infoContacto(_id) {
    var infoCont = candidatos.find((d) => d._id === _id);
    setInfoCon(infoCont._id);
  }

  function infoComentarios(_id) {
    var comments = candidatos.find((d) => d._id === _id);
    setComents(comments._id);
  } */

  if (!candidatos) {
    return (
      <div className="text-center">
        <div
          className="spinner-grow"
          style={{ width: "3rem", height: "3rem" }}
          role="status"
        >
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }
  return (
    <div>
      <h1 className="h1 my-5 text-center animate__animated animate__fadeInUp">
        Candidatos Releevant
      </h1>
      <div className="container">
        <div className="row justify-content-between">
          <div className="col-9">
            <Buscador />
          </div>
          <div className="col">
            <Link to="/anadir" className="btn btn-warning">
              Añadir nuevo candidato
            </Link>
          </div>
        </div>
      </div>
      {candidatos.map((candidato) => {
        return (
          <div className="container" key={candidato._id}>
            <div className="row my-5 m-auto">
              <div className="col-4">
                <div className="list-group" id="list-tab" role="tablist">
                  <button
                    /* onClick={() => datosPeronales(datos)} */
                    className="list-group-item list-group-item-action active"
                    id="list-home-list"
                    data-toggle="list"
                    href="#list-home"
                    role="tab"
                    aria-controls="home"
                  >
                    Datos personales
                  </button>
                  <button
                   /*  onClick={() => infoReleevant(infoRel)} */
                    className="list-group-item list-group-item-action"
                    id="list-profile-list"
                    data-toggle="list"
                    href="#list-profile"
                    role="tab"
                    aria-controls="profile"
                  >
                    Info Releevant
                  </button>
                  <button
                   /*  onClick={() => infoContacto(infoCon)} */
                    className="list-group-item list-group-item-action"
                    id="list-messages-list"
                    data-toggle="list"
                    href="#list-messages"
                    role="tab"
                    aria-controls="messages"
                  >
                    Info contacto
                  </button>
                  <button
                    /* onClick={() => infoComentarios(coments)} */
                    className="list-group-item list-group-item-action"
                    id="list-settings-list"
                    data-toggle="list"
                    href="#list-settings"
                    role="tab"
                    aria-controls="settings"
                  >
                    Comentarios
                  </button>
                </div>
              </div>
              <div className="col-8">
                <div className="tab-content" id="nav-tabContent">
                  <div
                    className="tab-pane fade show active"
                    id="list-home"
                    role="tabpanel"
                    aria-labelledby="list-home-list"
                  >
                    <h3>
                      {" "}
                      <b>{candidato.nombre}</b>
                    </h3>
                    <p>
                      <b>Telefono</b>: {candidato.telefono}
                    </p>
                    <p>
                      <b>Email</b>: {candidato.email}
                    </p>
                    <p>
                      <b>Edad</b>: {candidato.edad}
                    </p>
                    <p>
                      <b>Estudios</b>: {candidato.estudios}
                    </p>
                    <p>
                      <b>Profesion</b>: {candidato.profesion}
                    </p>
                  </div>
                  <div
                    className="tab-pane fade"
                    id="list-profile"
                    role="tabpanel"
                    aria-labelledby="list-profile-list"
                  >
                    <h3>
                      {" "}
                      <b>{candidato.cursoInteres}</b>
                    </h3>
                    <p>
                      <b>Fecha de entrada</b>: {candidato.fechaEntrada}
                    </p>
                    <p>
                      <b>Origen</b>: {candidato.origen}
                    </p>
                    <p>
                      <b>Estado</b>: {candidato.estado}
                    </p>
                  </div>
                  <div
                    className="tab-pane fade"
                    id="list-messages"
                    role="tabpanel"
                    aria-labelledby="list-messages-list"
                  >
                    <h3> Info:</h3>
                    <p>
                      <b>Llamada echa</b>: {candidato.call}
                    </p>
                    <p>
                      <b>Cita</b>: {candidato.cita.fecha} a le{" "}
                      {candidato.cita.ora}
                    </p>
                    <p>
                      <b>Estado</b>: {candidato.estado}
                    </p>
                  </div>
                  <div
                    className="tab-pane fade"
                    id="list-settings"
                    role="tabpanel"
                    aria-labelledby="list-settings-list"
                  >
                    <h3>Comentarios:</h3>
                    {candidato.detalles}
                  </div>
                </div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default Candidatos;
