import React, { useState, useEffect } from "react";
import "../App.css";
import { Link } from "react-router-dom";
import "animate.css";

function Docentes() {
  const [docentes, setDocentes] = useState([]);

  useEffect(function () {
    async function fetchApi() {
      let response = await fetch("http://localhost:8080/docentes", {
        mode: "cors",
      });
      let json = await response.json();
      setDocentes(json);
    }

    fetchApi();
  }, []);

  async function handleDelete(_id) {
    let response = await fetch(`http://localhost:8080/delete/docente/${_id}`, {
      mode: "cors",
      method: "DELETE",
    });
    let json = await response.json();
    setDocentes(json);
  }


  if (!docentes) {
    return (
      <div className="text-center">
        <div
          className="spinner-grow"
          style={{ width: "3rem", height: "3rem" }}
          role="status"
        >
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }
  return (
    <div className="container">
      <div className="row mb-5">
        <h1 className="h1 my-5 text-center animate__animated animate__fadeInUp">
          Docentes Releevant
        </h1>
        <div className="container">
          <div className="row justify-content-between">
            <div className="col-9"></div>
          </div>
        </div>

        {docentes?.map((docente) => {
          return (
            <div
              className="col d-flex justify-content-around"
              key={docente._id}
            >
              <div className="card mb-5" style={{ width: "19rem" }}>
                <img
                  src={docente.foto}
                  className="card-img-top embed-responsive-item"
                  alt="..."
                  width={"15%"}
                />
                <div className="card-body">
                  <h5 className="card-title">{docente.nombre}</h5>
                  <h6>{docente.posicion}</h6>
                  <p className="card-text">{docente.bio}</p>
                  <button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModalCenter">
  See full description
</button>


<div class="modal fade" id="exampleModalCenter" tabindex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
  <div class="modal-dialog modal-dialog-centered" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLongTitle">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
                  <a href={docente.LinkedIn}>LinkedIn</a>
                  <button
              onClick={() => handleDelete(docente._id)}
              className="btn btn-secondary w-100 my-3"
            >
              Delete docente
            </button>
                </div>
              </div>
            </div>
          );
        })}
      </div>
      <Link to="/anadirdocente" className="btn btn-warning w-100 mb-5">
        Añadir nuevo docente
      </Link>
    </div>
  );
}
export default Docentes;
