import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Buscador from "../components/Buscador";

function Candidatos() {
  const [candidatos, setCandidatos] = useState([]);
  const [datos, setDatos] = useState();

  useEffect(function () {
    async function fetchApi() {
      let response = await fetch("http://localhost:8080/candidatos", {
        mode: "cors",
      });
      let json = await response.json();
      setCandidatos(json);
    }

    fetchApi();
  }, []);

  function datosPersonales(_id) {
    var datosPer = candidatos.find((d) => d._id === _id);
    setDatos(datosPer);
  }

  console.log(datos);
  if (!candidatos) {
    return (
      <div className="text-center">
        <div
          className="spinner-grow"
          style={{ width: "3rem", height: "3rem" }}
          role="status"
        >
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }
  return (
    <div>
      <h1 className="h1 my-5 text-center animate__animated animate__fadeInUp">
        Candidatos Releevant
      </h1>
      <div className="container">
        <div className="row justify-content-between">
          <div className="col-9">
            <Buscador />
          </div>
          <div className="col">
            <Link to="/anadir" className="btn btn-warning">
              AÃ±adir nuevo candidato
            </Link>
          </div>
        </div>
      </div>
      {candidatos.map((candidato) => {
        return (
          <div className="container">
            <div className="card" style={{width: "18rem"}}>
              <div className="card-header"><h4>{candidato.nombre}</h4></div>
              <ul className="list-group list-group-flush">
                <li className="list-group-item">
                <p>
                    <b>Telefono</b>:{" "}
                    {candidato.telefono}
                  </p>
                  <p>
                    <b>Email</b>:{" "}
                    {candidato.email}
                  </p>
                </li>
                <li className="list-group-item">A second item</li>
                <li className="list-group-item">A third item</li>
              </ul>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default Candidatos;
