import React, { useState, useEffect } from "react";
import "../App.css";
import { useParams, Link, useNavigate } from "react-router-dom";
import "animate.css";

function Docentes() {
  const [docentes, setDocentes] = useState([]);

  let navigate = useNavigate();


  useEffect(function () {
    async function fetchApi() {
      let response = await fetch("http://localhost:8080/docentes", {
        mode: "cors",
      });
      let json = await response.json();
      setDocentes(json);
    }

    fetchApi();
  }, []);

  async function handleDelete(_id) {
    let response = await fetch(`http://localhost:8080/delete/docente/${_id}`, {
      mode: "cors",
      method: "DELETE",
    });
    let json = response.json();
    setDocentes(json);
  }


  if (!docentes) {
    return (
      <div className="text-center">
        <div
          className="spinner-grow"
          style={{ width: "3rem", height: "3rem" }}
          role="status"
        >
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }
  return (
    <div className="container">
      <div className="row mb-5">
        <h1 className="h1 my-5 text-center animate__animated animate__fadeInUp">
          Docentes Releevant
        </h1>
        <div className="container">
          <div className="row justify-content-between">
            <div className="col-9"></div>
          </div>
        </div>

        {docentes?.map((docente) => {
          return (
            <div
              className="col d-flex justify-content-around"
              key={docente._id}
            >
              <div className="card mb-5" style={{ width: "19rem" }}>
                <img
                  src={docente.foto}
                  className="card-img-top embed-responsive-item"
                  alt="..."
                  width={"15%"}
                />
                <div className="card-body">
                  <h5 className="card-title">{docente.nombre}</h5>
                  <h6>{docente.posicion}</h6>
                  <p className="card-text">{docente.bio}</p>
                  <a href={docente.LinkedIn}>LinkedIn</a>
                  <button
              onClick={() => handleDelete(docente._id)}
              className="btn btn-secondary w-100 my-3"
            >
              Delete docente
            </button>
                </div>
              </div>
            </div>
          );
        })}
      </div>
      <Link to="/anadir" className="btn btn-warning w-100 mb-5">
        Añadir nuevo docente
      </Link>
    </div>
  );
}
export default Docentes;
