import { useState, useEffect } from "react";
import {useGlobalContext} from "../contexts/GlobalContext"

export default function Search() {
  const { setAlumnos,setAlumni,setCandidatos,setEventos } = useGlobalContext();
  const [inputText, setInputText] = useState("");

  function search(e) {
    setInputText(e.target.value);
  }

  useEffect(
    function () {
      async function fetchApi() {
        let url = encodeURI(`http://localhost:8080/busca/${inputText}`);
        let response = await fetch(url, {
          mode: "cors",
        });
        let json = await response.json();
        setAlumnos(json);
      }
      async function fetchAlumnos() {
        let response = await fetch("http://localhost:8080/", {
          mode: "cors",
        });
        let json = await response.json();
        setAlumnos(json);
      }
      if (inputText !== "") {
        fetchApi();
      }
      if (inputText === "") {
        fetchAlumnos();
      }
    },
    [inputText, setAlumnos]
  );

  useEffect(
    function () {
      async function fetchApi() {
        let url = encodeURI(`http://localhost:8080/buscar/alumni/${inputText}`);
        let response = await fetch(url, {
          mode: "cors",
        });
        let json = await response.json();
        setAlumni(json);
      }
      async function fetchAlumni() {
        let response = await fetch("http://localhost:8080/alumni", {
          mode: "cors",
        });
        let json = await response.json();
        setAlumni(json);
      }
      if (inputText !== "") {
        fetchApi();
      }
      if (inputText === "") {
        fetchAlumni();
      }
    },
    [inputText, setAlumni]
  );

  useEffect(
    function () {
      async function fetchApi() {
        let url = encodeURI(`http://localhost:8080/busca/candidatos/${inputText}`);
        let response = await fetch(url, {
          mode: "cors",
        });
        let json = await response.json();
        setCandidatos(json);
      }
      async function fetchCandidatos() {
        let response = await fetch("http://localhost:8080/candidatos", {
          mode: "cors",
        });
        let json = await response.json();
        setCandidatos(json);
      }
      if (inputText !== "") {
        fetchApi();
      }
      if (inputText === "") {
        fetchCandidatos();
      }
    },
    [inputText, setCandidatos]
  );

  useEffect(
    function () {
      async function fetchApi() {
        let url = encodeURI(`http://localhost:8080/buscar/eventos/${inputText}`);
        let response = await fetch(url, {
          mode: "cors",
        });
        let json = await response.json();
        setEventos(json);
      }
      async function fetchEventos() {
        let response = await fetch("http://localhost:8080/eventos", {
          mode: "cors",
        });
        let json = await response.json();
        setEventos(json);
      }
      if (inputText !== "") {
        fetchApi();
      }
      if (inputText === "") {
        fetchEventos();
      }
    },
    [inputText, setEventos]
  ); 

  return (
    <div>
      <div className="input-group rounded w-25">
      <form className="d-flex mb-5">
      <span className="input-group-text border-0" id="search-addon">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="16"
                height="16"
                fill="currentColor"
                className="bi bi-search"
                viewBox="0 0 16 16"
              >
                <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z" />
              </svg>
              </span>
        <input
          onChange={search}
          className="form-control m w-75 m-auto"
          type="search"
          placeholder="Buscar"
          aria-label="Search"
          value={inputText}
        />
       
            
      </form>
      </div>
    </div>
  );
}
