import React from "react";
import "../App.css";
import { Link } from "react-router-dom";
import { useGlobalContext } from "../contexts/GlobalContext";
import Buscador from "../components/Buscador";
import "animate.css";

function Alumnos() {
  const { alumnos } = useGlobalContext();

  if (!alumnos) {
    return (
      <div className="text-center">
        <div
          className="spinner-grow"
          style={{ width: "3rem", height: "3rem" }}
          role="status"
        >
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }
  return (
    <div className="container">
      <div className="row mb-5">
      <h1 className="h1 my-5 mx-5 animate__animated animate__fadeInUp">
                Alumnos Releevant
              </h1>
        <div className="container">
          <div className="row justify-content-between">
            <div className="col-8">
            <Buscador />
            </div>
            <div className="col">
              <Link to="/anadir" className="btn btn-outline-warning my-5">
                AÃ±adir nuevo alumno
              </Link>
            </div>
          </div>
        </div>
        
        {alumnos?.map((alumno) => {
          return (
            <div className="col d-flex justify-content-around" key={alumno._id}>
              <div className="card mb-5" style={{ width: "19rem" }}>
                <img
                  src={alumno.datosPersonales.foto}
                  className="card-img-top embed-responsive-item"
                  alt="..."
                  width={"15%"}
                />
                <div className="card-body">
                  <h5 className="card-title">
                    {alumno.datosPersonales.nombre}
                  </h5>
                  <p className="card-text">{alumno.curso}</p>
                  <Link
                    to={`detalles/${alumno._id}`}
                    className="btn btn-outline-dark w-100 m-auto"
                  >
                    Details
                  </Link>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Alumnos;
