import React from "react";
import "../App.css";
import { useParams, Link, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

function Detalles() {
  const [alumno, setAlumno] = useState(null);
  const { _id } = useParams();

  let navigate = useNavigate();

  useEffect(
    function () {
      async function fetchApi() {
        let response = await fetch(`http://localhost:8080/buscar/${_id}`, {
          mode: "cors",
        });
        let json = await response.json();
        console.log(json);
        setAlumno(json);
      }
      fetchApi();
    },
    [_id]
  );

  async function handleDelete(_id) {
    let response = await fetch(`http://localhost:8080/delete/alumno/${_id}`, {
      mode: "cors",
      method: "DELETE",
    });
    let json = response.json();
    setAlumno(json);
    navigate("/");
  }

  if (!alumno) {
    return (
      <div className="text-center my-5">
        <div
          className="spinner-grow"
          style={{ width: "3rem", height: "3rem" }}
          role="status"
        >
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }
  return (
    <div className="container">
      <div className="row">
        <div className="col">
          <img
            src={alumno[0].alumno.datosPersonales.foto}
            className="mx-5 my-5"
            alt="foto de alumno"
            width={"70%"}
          />
          <div className="">
            <Link
              to={`/update/${_id}`}
              className="btn btn-secondary w-100 my-3"
            >
              Update Alumno
            </Link>
            <button
              onClick={() => handleDelete(_id)}
              className="btn btn-warning w-100 mb-5"
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="16"
                height="16"
                fill="currentColor"
                className="bi bi-trash mr-3"
                viewBox="0 0 16 16"
              >
                <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V6z" />
                <path
                  fill-rule="evenodd"
                  d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1zM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118zM2.5 3V2h11v1h-11z"
                />
              </svg>
              Delete Alumno
            </button>
          </div>
        </div>
        <div className="col float-end mx-5 my-5">
          <h3 className="my-4">
            <b>{alumno[0].alumno.datosPersonales.nombre}</b>
          </h3>
          <div className="accordion" id="accordionExample">
            <div className="accordion-item">
              <h2 className="accordion-header" id="headingOne">
                <button
                  className="accordion-button"
                  type="button"
                  data-bs-toggle="collapse"
                  data-bs-target="#collapseOne"
                  aria-expanded="true"
                  aria-controls="collapseOne"
                >
                  Datos Personales
                </button>
              </h2>
              <div
                id="collapseOne"
                className="accordion-collapse collapse show"
                aria-labelledby="headingOne"
                data-bs-parent="#accordionExample"
              >
                <div className="accordion-body">
                  <p>
                    <b>Curso</b>: {alumno[0].alumno.curso}
                  </p>
                  <p>
                    <b>Fecha de nacimiento</b>:{" "}
                    {alumno[0].alumno.datosPersonales.fechaNacimiento}
                  </p>
                  <p>
                    <b>Residencia</b>:{" "}
                    {alumno[0].alumno.datosPersonales.residencia}
                  </p>
                  <p>
                    <b>Telefono</b>: {alumno[0].alumno.datosPersonales.telefono}
                  </p>
                  <p>
                    <b>Email</b>: {alumno[0].alumno.datosPersonales.email}
                  </p>
                </div>
              </div>
            </div>
            <div className="accordion-item">
              <h2 className="accordion-header" id="headingTwo">
                <button
                  className="accordion-button collapsed"
                  type="button"
                  data-bs-toggle="collapse"
                  data-bs-target="#collapseTwo"
                  aria-expanded="false"
                  aria-controls="collapseTwo"
                >
                  Info Bootcamp
                </button>
              </h2>
              <div
                id="collapseTwo"
                className="accordion-collapse collapse"
                aria-labelledby="headingTwo"
                data-bs-parent="#accordionExample"
              >
                <div className="accordion-body">
                  <p>
                    <b>Fecha de falta</b>: <br />
                    {alumno[0].alumno.infoBootcamp.fechaFalta}{" "}
                  </p>
                  <p>
                    {" "}
                    <b>Proyecto Final</b>:{" "}
                    {alumno[0].alumno.infoBootcamp.proyectoFinal.titulo}{" "}
                  </p>
                  <p>
                    <b>Detalles</b>:{" "}
                    {alumno[0].alumno.infoBootcamp.proyectoFinal.detalles}{" "}
                  </p>
                  <p>
                    <b>Comentarios Profesorado</b>:{" "}
                    {
                      alumno[0].alumno.infoBootcamp.proyectoFinal
                        .comentariosProf
                    }{" "}
                  </p>
                </div>
              </div>
            </div>
            <div className="accordion-item">
              <h2 className="accordion-header" id="headingThree">
                <button
                  className="accordion-button collapsed"
                  type="button"
                  data-bs-toggle="collapse"
                  data-bs-target="#collapseThree"
                  aria-expanded="false"
                  aria-controls="collapseThree"
                >
                  Info Releevant
                </button>
              </h2>
              <div
                id="collapseThree"
